import { defineStore } from "pinia";
import { useStore } from "./mainStore";
import { ref, watch } from "vue";

const store = useStore();

export const productsStore = defineStore("product", () => {
  let checkedProduct = ref([]);
  let products = ref([]);
  let selectedItems = ref([]);
  let text = ref('')
  let searchOptions = ref([])


  async function getProducts() {
    const checkedList = checkedProduct._rawValue.join(",");
    if (checkedList) {
      const res = await fetch(
        `http://10.10.1.74:80/api/v1/catalog/search/categories-product?categories=${checkedList}&search=${text.value}`,
        {
          headers: store.headers,
        }
      );
      const data = await res.json();
      products.value = await data;


    } else {
      products.value = [];
    }
  }

  async function searchProduct() {

      const res = await fetch(
        `http://10.10.1.74:80/api/v1/catalog/search/categories-product?search=${text.value}`,
        {
          headers: store.headers,
        }
      );
      const data = await res.json();
      console.log(text.value)
      console.log(data)
      for(let option of  data) {
        searchOptions.value.push({value: option.attributes.full_name})
      }

  }

  function resetStore() {
    checkedProduct.value = [];
    products.value = [];
    selectedItems.value = []
  }

  watch(checkedProduct, () => {
    getProducts();
  });

  watch(text, ()=> {
    searchProduct()
  })

  return {
    searchProduct,
    resetStore,
    getProducts,
    products,
    checkedProduct,
    selectedItems,
    text
  };
});
